package main

import (
    "errors"
    "fmt"
)


type queue []Point

func (q queue) Empty() bool {
    return len(q) == 0
}

func (q *queue) Put(p Point) {
    if q.Empty() {
        *q = []Point{p}
    } else {
        *q = append(*q, p)
    }
}

func (q *queue) First() (Point, error) {
    if q.Empty() {
        return Point{}, errors.New("empty queue")
    }
    firstValue := (*q)[0]
    *q = (*q)[1:]
    return firstValue, nil
}

type Point struct {
    i int
    j int
}

func validMove(p Point, g [][]int) bool {
    if p.i >= 0 && p.i < len(g) && p.j >= 0 && p.j < len(g[p.i]) {
        if g[p.i][p.j] == 1 {
            return true
        }
    }
    return false
}

func calculateIslandArea(i, j int, g *[][]int) int {
    area := 0
    var q queue
    q.Put(Point{
        i: i,
        j: j,
    })
    for {
        if q.Empty() {
            break
        }
        node, _ := q.First()
        if (*g)[node.i][node.j] == 1 {
            area++
            (*g)[node.i][node.j] = 0
            top := Point{ i: node.i, j: node.j - 1 }
            bot := Point{ i: node.i, j: node.j + 1 }
            left := Point{ i: node.i - 1, j: node.j }
            right := Point{ i: node.i + 1, j: node.j }
            if validMove(top, *g) {
                q.Put(top)
            }
            if validMove(bot, *g) {
                q.Put(bot)
            }
            if validMove(left, *g) {
                q.Put(left)
            }
            if validMove(right, *g) {
                q.Put(right)
            }
        }
    }
    return area
}



func maxAreaOfIsland(grid [][]int) int {
    maxArea := 0
    for i := 0; i < len(grid); i++ {
        for j := 0; j < len(grid[i]); j++ {
            node := grid[i][j]
            if node == 1 {
                area := calculateIslandArea(i, j, &grid)
                if area > maxArea {
                    maxArea = area
                }
            }
        }
    }
    return maxArea
}

